mapper.select:
SELECT no, title, content, regdate, hit, memberNo, email, type, likesNum, commentsNum FROM(SELECT no, title, content, regdate, hit, memberNo,email, type, likesNum, commentsNum, rownum r FROM (SELECT b.no no, b.title title, b.content content, b.regdate regdate, b.hit hit,b.type type, m.no memberNo, l.count likesNum,email, c.count commentsNum FROM board b, members m, (SELECT postNum,COUNT(*) count FROM likes WHERE type='F' GROUP BY postNum) l, (SELECT postNum,COUNT(*) count FROM comments WHERE type='F' GROUP BY postNum) c WHERE b.member_no = m.no AND b.no = l.postNum(+) AND b.no = c.postNum(+) AND type='F'
AND title Like #{wire}
AND content Like #{wire}
AND email Like #{wire}
ORDER BY ${order} DESC nulls last)) WHERE r BETWEEN #{start} AND #{end}

SELECT no, title, content, regdate, hit, memberNo, email, type, likesNum, commentsNum FROM(SELECT no, title, content, regdate, hit, memberNo,email, type, likesNum, commentsNum, rownum r FROM (SELECT b.no no, b.title title, b.content content, b.regdate regdate, b.hit hit,b.type type, m.no memberNo, l.count likesNum,email, c.count commentsNum FROM board b, members m, (SELECT postNum,COUNT(*) count FROM likes WHERE type='S' GROUP BY postNum) l, (SELECT postNum,COUNT(*) count FROM comments WHERE type='S' GROUP BY postNum) c WHERE b.member_no = m.no AND b.no = l.postNum(+) AND b.no = c.postNum(+) AND type='S'
AND title Like #{wire}
AND content Like #{wire}
AND email Like #{wire}
ORDER BY ${order} DESC nulls last)) WHERE r BETWEEN #{start} AND #{end}

mapper.update:
UPDATE board 
SET hit = hit+1
WHERE no = #{no}
